import { EllipsisOutlined } from "@ant-design/icons";
import { Dropdown, Image, Tooltip } from "antd";
import dayjs from "dayjs";
import _ from "lodash";

const imgErr =
  "data:image/png;base64,";
// columns.js

const getColumns = (onActionClick, currentPage, pageSize) => [
  {
    title: "STT",
    fixed: "left",
    align: "center",
    width: "70px",
    render: (_, record, index) => (currentPage - 1) * pageSize + index + 1,
  },
  {
    title: "Tên sản phẩm",
    dataIndex: "name",
    key: "name",
    width: "150px",
  },
  {
    title: "Ảnh sản phẩm",
    key: "image",
    render: (record) => {
      const imgProducts = _.get(record, "images", []);
      const firstImage = _.first(imgProducts);
      return (
        <Image
          width={50}
          height={50}
          src={firstImage || imgErr}
          fallback={imgErr}
          alt="Ảnh sản phẩm"
        />
      );
    },
  },
  {
    title: "Mô tả sản phẩm",
    dataIndex: "description",
    key: "description",
    ellipsis: true,
    width: "300px",
  },
  {
    title: "Giá",
    dataIndex: "price",
    key: "price",
    align: "center",
  },
  {
    title: "Tồn kho",
    key: "stock",
    dataIndex: "stock",
    align: "center",
  },
  {
    title: "Ngày tạo",
    dataIndex: "createdAt",
    key: "createdAt",
    align: "center",
    render: (value) => dayjs(value).format("DD/MM/YYYY"),
  },
  {
    title: "Thao tác",
    key: "action",
    align: "center",
    render: (record) => {
      const menuItems = [
        {
          key: "view",
          label: "Xem",
        },
        {
          key: "edit",
          label: "Sửa",
        },
        {
          key: "delete",
          label: "Xóa",
        },
      ];

      return (
        <Dropdown
          menu={{
            items: menuItems,
            onClick: ({ key }) => onActionClick(key, record), // ✅ Truyền key và record về
          }}
          trigger={["click"]}
        >
          <Tooltip title="Thao tác">
            <EllipsisOutlined style={{ fontSize: 20, cursor: "pointer" }} />
          </Tooltip>
        </Dropdown>
      );
    },
  },
];

export { imgErr, getColumns };
